package com.greggs.config;public enum MobileDevice {    IPHONE_12(390, 844),    IPHONE_13(390, 844),    SAMSUNG_GALAXY_S21(360, 800),    PIXEL_5(393, 851);    public final int width;    public final int height;    MobileDevice(int width, int height) {        this.width = width;        this.height = height;    }    @Override    public String toString() {        return name() + " (" + width + "x" + height + ")";    }}